import Input from "../../UI/Input";
import classes from "./BoardItemForm.module.css";
const BoardItemForm = (props) => {
  return (
    <form className={classes.form}>
      <Input
        label="Amount"
        input={{
          id: "amount_" + props.id,
          type: "number",
          min: "1",
          max: "5",
          step: "1",
          defaultValue: "1",
          /*기본내장props*/
        }}
      />
      <button>장바구니에 추가</button>
    </form>
  );
};

export default BoardItemForm;
